// variant of https://shadertoy.com/view/MllBWs

#define rnd(U) texture(iChannel0, U)
vec4 rndC(vec2 U) {                       // bicubic interpolation         
    vec2 R = iChannelResolution[0].xy;
    U = U*R-.5;
    vec2 F = fract(U); F = F*F*(3.-2.*F); // hermite spline 0->1
    #define T(x,y) texelFetch(iChannel0, ivec2(mod( U +vec2(x,y), R)), 0)
    return mix( mix(T(0,0),T(1,0),F.x), mix(T(0,1),T(1,1),F.x), F.y );
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O -= O; 
    U = ( U+U - R ) / R.y;
	float t = 0.*iTime,
          a = atan(U.y,U.x) + .1*t, r = length(U);           // to polar
    vec4 N;
  //N = rndC( vec2(.4*r,a/6.283)); r+=.0*N.x; a+=.3*N.y; 
    N = rndC( .1*U); r+=.15*N.x; a+=.6*N.y; 
    r += 1.2*sin(3.*r*6.28)/18.8 * mix( 1., sin(7.*a) , 1.); // base pattern of sliced stem-leaves
  //N = rndC( vec2(.4*r,a/6.283)); r+=.04*N.x; a+=.6*N.y;     // perturbations (which space is best ?)
  //N = rndC( .3*U); r+=.04*N.x; a+=.6*N.y; 
  //O = N; return;
    O += .5+.5*sin( 100.*r + 2.*a + 2.*t) ;                  // double spiral
    O *=  vec4(.6,.3,1,1) * 3.2;                             // coloring scheme
}
