vec2 spiral(vec2 uv)
{
    float turns = 4.0+saw(time/4.0)*4.0;
    float r = pow(log(length(uv)+1.), .75);
    float theta = atan(uv.y, uv.x)*turns-r*PI;
    return vec2(saw(r*PI+theta/turns+iTime*.2), saw(theta/turns+iTime*.1));
}

