    square = RotateZ(square, time+seedling);

    square = scale(square, vec2(simplex3d(vec3(uv.x+seedling+time,
                                               uv.y+seedling+time,seedling+time)))+.75);

    square = scale(square, vec2(.5));
    square = translate(square, vec2(.5));


    float t = time;
