float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

// copy from https://www.shadertoy.com/view/XljGzV
// thanks!
vec3 rgb2hsl( in vec3 c ){
  float h = 0.0;
	float s = 0.0;
	float l = 0.0;
	float r = c.r;
	float g = c.g;
	float b = c.b;
	float cMin = min( r, min( g, b ) );
	float cMax = max( r, max( g, b ) );

	l = ( cMax + cMin ) / 2.0;
	if ( cMax > cMin ) {
		float cDelta = cMax - cMin;

        //s = l < .05 ? cDelta / ( cMax + cMin ) : cDelta / ( 2.0 - ( cMax + cMin ) ); Original
		s = l < .0 ? cDelta / ( cMax + cMin ) : cDelta / ( 2.0 - ( cMax + cMin ) );

		if ( r == cMax ) {
			h = ( g - b ) / cDelta;
		} else if ( g == cMax ) {
			h = 2.0 + ( b - r ) / cDelta;
		} else {
			h = 4.0 + ( r - g ) / cDelta;
		}

		if ( h < 0.0) {
			h += 6.0;
		}
		h = h / 6.0;
	}
	return vec3( h, s, l );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord.xy / iResolution.xy;	
    vec4 color = texture(iChannel0, uv);	
    float v = mod(rand(uv)+iTime, 1.0);

    fragColor = color;
    vec3 hsl = rgb2hsl(vec3(color.x, color.y, color.z));
    if ( abs(hsl.x) - 0.0823 < 0.1 )
    {
        if ( abs(hsl.y) - 0.5058 < 0.1 )
        {            
            if ( hsl.z > 0.0296 && hsl.z < 0.8274 )
            {
        		fragColor = vec4(vec3(v), 1.0);                
            }
        }
    }
}
