vec3 hs2rgb(vec2 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 colorFromIndex(float index)
{
#if 1
    index -= 2.0/255.0;
	//index /= 253.0/255.0;
#endif
    return hs2rgb(vec2(fract((index * 12.0)), index));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float tileX = floor(uv.x*16.0);
    float tileY = floor(uv.y*16.0);
    float tileId = tileY*16.0+tileX;
	fragColor = vec4(pow(colorFromIndex(tileId/256.0), vec3(0.4545)),1.0);    
}
