#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord.xy / iResolution.xy;

    float proportion = iResolution.x/iResolution.y;


    vec4 sharpImageColor = texture(iChannel0, uv);
    vec4 blurredImageColor = texture(iChannel1, uv);    

    //控制x方向 y不变，x越大越趋近于直线
    float x = 0.0;
    float proportionX = proportion* (1.0-x);

    //控制y方向 x不变，y越大越趋近于直线
    float y = 0.0;
    float proportionY = 1.0 - y;

    //x = y 时候就等于0

    float excludeCircleRadius = .15;
    //模糊过渡的
    float excludeBlurSize = 0.0;



    vec2 excludeCirclePoint = vec2(0.5,0.5);
    excludeCirclePoint = excludeCirclePoint *rot((iTime));
    excludeCirclePoint.x = excludeCirclePoint.x *proportionX-proportionX/2.0+0.5;
    excludeCirclePoint.y = excludeCirclePoint.y *proportionY-proportionY/2.0+0.5;




    vec2 textureCoordinateToUse = vec2(uv.x,uv.y);  
    textureCoordinateToUse = textureCoordinateToUse *rot((iTime));
    textureCoordinateToUse.x = textureCoordinateToUse.x *proportionX-proportionX/2.0+0.5;
    textureCoordinateToUse.y = textureCoordinateToUse.y *proportionY-proportionY/2.0+0.5;


    float distanceFromCenter = distance(excludeCirclePoint, textureCoordinateToUse);    

    fragColor = mix(sharpImageColor, blurredImageColor, smoothstep(excludeCircleRadius - excludeBlurSize, excludeCircleRadius, distanceFromCenter));


}
