mat3 rotX(float d){
    float s = sin(d);
    float c = cos(d);
    return mat3(1.0, 0.0, 0.0,
                0.0,   c,  -s,
                0.0,   s,   c );
}

mat3 rotY(float d){
    float s = sin(d);
    float c = cos(d);
    return mat3(  c, 0.0,  -s,
                0.0, 1.0, 0.0,
                  s, 0.0,   c );
}

float closeObj = 0.0;

vec2 vecMin(vec2 a, vec2 b){
    if(a.x <= b.x){
        return a;
    }
    return b;
}

vec2 mapMat(vec3 p){
    vec3 q = p;
    vec3 r = p;
    if(mod(floor(p / 3.0), 2.0) == vec3(0.0, 0.0, 0.0)){
        q += vec3(0.5 * sin(iTime), 0.5, 0.5 * cos(iTime));
    	r += vec3(-0.5 * sin(iTime), -0.5, -0.5 * cos(iTime));
    }else{
        q += vec3(-0.5 * sin(iTime), -0.5, -0.5 * cos(iTime));
    	r += vec3(0.5 * sin(iTime), 0.5, 0.5 * cos(iTime));
    }
    q = vec3(mod(q.x, 3.0) - 1.5, mod(q.y, 3.0) - 1.5, mod(q.z, 3.0) - 1.5);
    r = vec3(mod(r.x, 3.0) - 1.5, mod(r.y, 3.0) - 1.5, mod(r.z, 3.0) - 1.5);
    r *= rotY(iTime / 2.0) * rotX(iTime);

    vec2 sph = vec2(length(q) - 0.5, 1.0);
    vec2 cube = vec2(length(vec2(length(r.xz) - 0.5, r.y)) - 0.25, 2.0);

    return vecMin(sph, cube);
}

float map(vec3 p){
    return mapMat(p).x;
}

float trace(vec3 ro, vec3 rd){
    float t = 0.0;
    float d = 0.0;
    vec2 c;
    int inter = 0;
    for(int i = 0; i < 1000; i++){
        c = mapMat(ro + rd * t);
        d = c.x;
        if(d < 0.0001){
            inter = 1;
            break;
        }
        t += d;
        if(t > 30.0){
            break;
        }
    }
    closeObj = c.y;
    if(inter == 0){
        t = -1.0;
    }
    return t;
}

vec3 normal(vec3 p){
    return normalize(vec3(map(vec3(p.x + 0.0001, p.yz)) - map(vec3(p.x - 0.0001, p.yz)),
                          map(vec3(p.x, p.y + 0.0001, p.z)) - map(vec3(p.x, p.y - 0.0001, p.z)),
                	      map(vec3(p.xy, p.z + 0.0001)) - map(vec3(p.xy, p.z - 0.0001))));
}

vec3 camPos = vec3(0.0, 1.0, 0.0);
vec3 lightPos = vec3(0.0, 1.0, -1.0);

vec3 amb(vec3 c, float k){
    return c * k;
}

vec3 diff(vec3 c, float k, vec3 p){
    vec3 n = normal(p);
    vec3 l = normalize(lightPos - p);
    return c * k * max(0.0, dot(n, l));
}

vec3 spec(vec3 c, float k, vec3 p, float a){
    vec3 n = normal(p);
    vec3 l = normalize(lightPos - p);
    vec3 v = normalize(p - camPos);
    float facing = dot(l, n) > 0.0 ? 1.0 : 0.0;
    vec3 r = reflect(l, n);
    return c * k * facing * pow(max(0.0, dot(r, v)), a);
}

vec3 colour(vec3 p, float id){
    if(id == 1.0){
        vec3 col = vec3(0.0, 0.0, 1.0);
        return amb(col, 0.5) + diff(col, 0.9, p) + spec(vec3(1.0), 0.2, p, 8.0);
    }
    vec3 col = vec3(1.0, 0.0, 0.0);
    return amb(col, 0.5) + diff(col, 0.8, p) + spec(vec3(1.0), 0.9, p, 8.0);
} 

float shadow(vec3 ro, vec3 rd){
    float t = 0.5;
    float d = 0.0;
    float shadow = 1.0;
    for(int iter = 0; iter < 1000; iter++){
        d = map(ro + rd * t);
        if(d < 0.0001){
            return 0.0;
        }
        if(t > length(ro - lightPos) - 0.5){
            break;
        }
        shadow = min(shadow, 128.0 * d / t);
        t += d;
    }
    return shadow;
}

float lastx = 0.0;
float lasty = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    camPos = vec3(0.0 , 0.0, 0.0);
    lightPos = vec3(0.0, 0.0, 0.0);
    lastx += iMouse.x - 0.5;
    lasty += iMouse.y - 0.5;
    vec3 ro = camPos;
    vec3 rd = normalize(rotY(radians(lastx)) * rotX(radians(lasty)) * vec3(uv, 1.0));
    float d = trace(ro, rd);
    vec3 c = ro + rd * d;
    vec3 col = vec3(1.0);
    //If intersected
    if(d > 0.0){
    	col = colour(c, closeObj);
        float s = shadow(c, normalize(lightPos - c));
        col -= vec3(0.5 - 0.5 * s);
    	col *= 1.0 / exp(d * 0.1);
    }else{
        col = vec3(0.0);
    }
	fragColor = vec4(col,1.0);
}
