//Final Image
//Reads iChannel0 just for a reference 
//Reads iChannel1 for the blurred texture generated by BufA->BufB


//This is a chain of images.  Input Image -> BufA -> BufB -> Image 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    lowp vec2 uv = fragCoord.xy / iResolution.xy;


    lowp vec4 blurrySample = texture(iChannel1, uv);
    lowp vec4 regularSample = texture(iChannel0, uv);

    //Note: this awful chunk of code is simply for comparing the two inputs
    //You want the contents of BufA and BufB
    float x = iMouse.x;
    if (abs(fragCoord.x - x) < 1.0)
    {
		fragColor = vec4(0,1,0,1);
    }
    else
    {
		if (fragCoord.x < x)
        {
            fragColor = regularSample;
        }
        else
        {
            fragColor = blurrySample;
        }
    }

}

//Horizontal pass
//Reads iChannel0 
//Writes BufA

//varying lowp vec2 sampleUV0;
//varying lowp vec2 sampleUV1;  //etc 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Optimization tip: Don't calculate UVS in a fragment shader
    //Move these calculations to the vertex shader and pass through 7 uv coordinates using varying vec2s

    lowp vec2 fragSize = vec2(1.0 / iResolution.x, 0.0);  //X axis blur
    lowp vec2 uv = fragCoord.xy / iResolution.xy;

    lowp vec2 sampleUV0 = uv - (fragSize * 3.0);
    lowp vec2 sampleUV1 = uv - (fragSize * 2.0);
    lowp vec2 sampleUV2 = uv - fragSize;
    lowp vec2 sampleUV3 = uv;
    lowp vec2 sampleUV4 = uv + fragSize;
    lowp vec2 sampleUV5 = uv + (fragSize * 2.0);
    lowp vec2 sampleUV6 = uv + (fragSize * 3.0);


    lowp vec4 sample = texture(iChannel0, sampleUV0) * 0.00598;
    sample +=		   texture(iChannel0, sampleUV1) * 0.060626;
    sample +=		   texture(iChannel0, sampleUV2) * 0.241843;
    sample += 		   texture(iChannel0, sampleUV3) * 0.383103;
    sample +=          texture(iChannel0, sampleUV4) * 0.241843;
    sample +=          texture(iChannel0, sampleUV5) * 0.060626;
    sample +=          texture(iChannel0, sampleUV6) * 0.00598;


	fragColor = sample;
}

//Vertical pass
//Reads iChannel0
//Writes BufB

//varying lowp vec2 sampleUV0;
//varying lowp vec2 sampleUV1;  //etc 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Optimization tip: Don't calculate UVS in a fragment shader
    //Move these calculations to the vertex shader and pass through 7 uv coordinates using varying vec2s

    lowp vec2 fragSize = vec2(0.0, 1.0 / iResolution.y);  //Y axis blur
    lowp vec2 uv = fragCoord.xy / iResolution.xy;

    lowp vec2 sampleUV0 = uv - (fragSize * 3.0);
    lowp vec2 sampleUV1 = uv - (fragSize * 2.0);
    lowp vec2 sampleUV2 = uv - fragSize;
    lowp vec2 sampleUV3 = uv;
    lowp vec2 sampleUV4 = uv + fragSize;
    lowp vec2 sampleUV5 = uv + (fragSize * 2.0);
    lowp vec2 sampleUV6 = uv + (fragSize * 3.0);


    lowp vec4 sample = texture(iChannel0, sampleUV0) * 0.00598;
    sample +=		   texture(iChannel0, sampleUV1) * 0.060626;
    sample +=		   texture(iChannel0, sampleUV2) * 0.241843;
    sample += 		   texture(iChannel0, sampleUV3) * 0.383103;
    sample +=          texture(iChannel0, sampleUV4) * 0.241843;
    sample +=          texture(iChannel0, sampleUV5) * 0.060626;
    sample +=          texture(iChannel0, sampleUV6) * 0.00598;


	fragColor = sample;

}
