#define particles_count 40.
#define k 0.006
#define threshold 16.

#define zmul(a, b) vec2(a.x*b.x-a.y*b.y, a.x*b.y+b.x*a.y)
#define zinv(a) vec2(a.x, -a.y) / dot(a,a)

vec2 df(vec2 p)
{
	//return abs(p)-uSlider;
	vec2 tri = vec2(max(abs(p.x)+p.y,-p.y)); // tri
	vec2 circle = vec2(length(p)); // circle
	return mix(tri, circle, sin(iTime*0.2))-0.46;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
    vec2 m = iMouse.xy/si;
	float t = iTime;
    vec3 stars = vec3(0);

    vec2 uv = (g+g-si)/min(si.x,si.y) * 2.;
    uv.x -= 0.5;

    vec2 z = uv;
	vec2 c = vec2(0.7,0.8);

    float r = 0.;
	for (float i=0.;i<50.;i++)
	{
		if (r > threshold) break;
        r = dot(z,z);
		z = zmul(z, z) + uv;  

        vec3 col = mix(vec3(0.5,0,0.5), vec3(0,1,0.29), i/5.);

        vec3 acc = vec3(0);
        for (float j=0.;j<particles_count;j++)
        {
            float tt = t + j/(3.14159/(particles_count*k));
            vec2 b = vec2(cos(tt), sin(tt)) * sqrt(threshold);
        	acc += col/r/dot(df(z-b), df(z-b));
        }
        stars += acc / particles_count / 0.75;
    }

    fragColor = vec4(stars*0.3,1);
}
