// --- spline interpolation ( inspired from revers https://www.shadertoy.com/view/MlGSz3 )
vec2 interpolate(vec2 G1, vec2 G2, vec2 G3, vec2 G4, float t)
{
    vec2 A = G4-G1 + 3.*(G2-G3),
         B = 3.*(G1-2.*G2+G3),
         C = 3.*(G2-G1),
         D = G1;
    return t * (t * (t * A + B) + C) + D;
}

float line(vec2 p, vec2 a, vec2 b) 
{
	vec2 pa = p - a, ba = b - a;
	float l = dot(ba, ba),
         h0 = dot(pa, ba) / l,                     // parameterization of proj on line
          h = clamp(h0 , 0., 1.);                  // parameterization of proj on segment
    //if (l<1e-6) return 1e38;                     // degenerated line
    dh2 = dh1, dh1 = abs(h-h0)*length(ba);         // how much beyond (for sign desambiguation)
	vec2 d = pa - ba * h;                          // distance vector to segment
    float s = pa.y*ba.x > pa.x*ba.y  ? 1. : -1.;   // sign: -1 on the left
	return dir*s*dot(d,d); //length(d);            // optimization by deferring sqrt
}

float bezier( vec2 uv, vec2 A, vec2 B, vec2 C, vec2 D)    
{
    //float d = 1e5;                               // for correct sign desambiguation with prev
    vec2 p = A;
    for (float t = 1.; t <= N; t++) {
        vec2 q = interpolate(A, B, C, D, t/N);
        float l = line(uv, p, q);
        d = t==1. ? smin (d, l ) : smin0(d, l );   // t==1 :  same thing
		p = q;
	}
	return d;
}

void draw(float d, inout vec4 O) 
{
    float s = sign(d);
    d = min(sqrt(abs(d*contrast)),1.);             // optimization by deferring sqrt here
    if (fill>0.) { if (s<0.) O = mix(O, COL, d); } // paint inside only
        else                 O = mix(COL, O, d);
}

void mainImage(out vec4 O, vec2 U) 
{
    if (iFrame>0) {
        O = texture(iChannel0, U/iResolution.xy);
        return;
    }
    O = vec4(1);
    vec2 R = iResolution.xy;
    U.y = R.y-U.y; U /= R.x;
	SVG( U, O );
}
