/**/
#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))
#define loops 20
vec2 mirrorrep(vec2 uv) {
    return abs(mod(uv,2.0)-1.0)-0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 4.0*(fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    mat2 rotmat = rot(iTime*0.1);
    int i = 0;
    for(i = 0; i < loops; i++) {
        uv = (abs(uv+1.0)-1.0)*rotmat;
        if (min(uv.x,uv.y)>0.5)break;
    }

	fragColor = vec4(float(i)/float(loops));
}
