// inspired by https://www.facebook.com/photo.php?fbid=10213540067038752&set=pcb.10213540068238782&type=3&theater

#define TAU 6.28318531
#define hash(x) fract(sin(x)*1e4)
#define h2(x,m) hash(dot(mod(x,m),vec2(3,5)))
#define h3(x) hash(dot(mod(x,20.),vec3(3,5,7)))

float n2(vec2 x, vec2 m) {
    vec2 i=floor(x),
        f=smoothstep(0.,1.,fract(x));
    vec2 oi=vec2(0.,1.);
    return mix(
        mix(h2(i+oi.xx,m),h2(i+oi.yx,m),f.x),
        mix(h2(i+oi.xy,m),h2(i+oi.yy,m),f.x),
        f.y);
}

float n3(vec3 x) {
    vec3 i=floor(x),
        f=smoothstep(0.,1.,fract(x));
    vec2 oi=vec2(0.,1.);
    return mix(
        mix(
            mix(h3(i+oi.xxx),h3(i+oi.yxx),f.x),
            mix(h3(i+oi.xyx),h3(i+oi.yyx),f.x),
            f.y),
        mix(
            mix(h3(i+oi.xxy),h3(i+oi.yxy),f.x),
            mix(h3(i+oi.xyy),h3(i+oi.yyy),f.x),
            f.y),
        f.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    float ac=atan(uv.y,uv.x),
        lc=length(uv);
    uv+=(n3(vec3(uv*30.,iTime))-.5)*.02;
    float a=atan(uv.y,uv.x),
        l=length(uv);
    a+=sin(iTime+l*10.)*.2;
    vec3 c=mix(vec3(0.,.4,1.),vec3(0.,.8,1.),smoothstep(.6,.62,n2(vec2(a/TAU*80.,l*5.-iTime*.5),vec2(80.,1e4))+.2-l*.3));
    c-=smoothstep(.6,.62,n2(vec2(a/TAU*20.,l*30.-iTime*3.),vec2(20.,1e4))+l*.2)*.5;
    vec3 cc=mix(vec3(.9,.7,.1),vec3(1.,1.,.4),smoothstep(.5,.52,n3(vec3(ac/TAU*20.+sin(iTime*2.+lc*50.)*1.,lc*100.-iTime*1.,iTime))));
    c=mix(cc,c,smoothstep(.1,.11,lc));
	fragColor = vec4(c,1.);
}
