float nSin(float x) {
	return sin(x)*0.5+0.5;
}

void mainImage( out vec4 c , vec2 u ) {
    float t = iDate.w;
    u /= iResolution.xy;
    u += vec2(sin(t*0.4), sin(t*0.6))*0.03;

    u.x = (u.x - 0.5)*(1.0+sin(t*0.4)*0.15) + 0.5;
    u.y = (u.y - 0.5)*(1.0+sin(t*0.1)*0.15) + 0.5;

    u.x += sin(u.y*3.0+t)*0.02;
    u.y += cos(u.x*4.5+t)*0.02;

    float w = sin(t*0.5)*0.2;
    vec2 p = u - 0.5;
	vec2 pu = vec2(length(p), atan(p.y, p.x));
    pu.x *= (1.0/w*clamp(pu.x, 0.0, w));
    u = vec2(pu.x*cos(pu.y), pu.x*sin(pu.y))+0.5;


    float vibration = nSin(t*0.8)*nSin(t*0.2);
    vec2 vibU = u;
	vibU.x += vibration*sin(t*99.0)*vibration*0.03;
    vibU.y += vibration*sin(t*24.0)*vibration*0.03;


    c = texture(iChannel0, u);
    float r1 = 0.5*sin(t*4.0)+0.6;
    float g1 = 0.1*sin(t*2.0)+0.8;
    float b1 = 0.2*sin(iDate.w*3.51)+0.2;
    float t1 = 0.5*sin(iDate.w)+1.0;
    float rc1 = 0.5*sin(iDate.w*4.0)+0.6;
    float gc1 = 0.1*sin(iDate.w*2.0)+0.8;
    float bc1 = 0.2*sin(iDate.w*3.51)+0.2;
    if (r1*c.r + g1*c.g + b1*c.b > t1) {
    	c.r *= rc1;
        c.g *= gc1;
        c.b *= bc1;
    }

    float r2 = 0.3*sin(t*4.0)-0.3;
    float g2 = 0.2*sin(t*2.0)+0.8;
    float b2 = 0.1*sin(t*3.51)+0.2;
    float t2 = 0.3*sin(t*0.9)+0.9;
    float rc2 = 0.1*sin(iDate.w*2.0)+0.6;
    float gc2 = 0.2*sin(iDate.w*1.0)+0.8;
    float bc2 = 0.4*sin(iDate.w*0.51)+0.2;
    if (r2*c.r + g2*c.g + b2*c.b < t1) {
    	c.r *= rc2;
        c.g *= gc2;
        c.b *= bc2;
    }

	vec4 vibSamp = texture(iChannel0, vibU);
    c += vibSamp*vibration;
}
