#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //sets 0 as the centre of the screen 
	vec2 r = vec2( fragCoord.xy - 0.5*iResolution.xy );
    r = 2.0 * r.xy / iResolution.xy;

    //variables
    float x = r.x;
    float y = r.y;
    float t = iTime * 3.2;
  	float adj = 1.1;
    float h = 0.3;

    //Colors
    vec3 red = vec3(1,0,0);
    vec3 green = vec3(0,1,0);
    vec3 blue = vec3(0,0,1);
    vec3 pixelCol = vec3(0, 0, 0);

    if(1.0 * abs((h * (x + adj)) * sin(PI*((x + 1.0) * t)) - y) < 0.02 ) pixelCol = red;
    if(1.0 * abs((h * (x - adj)) * cos(PI*((x - 1.0) * t)) - y) < 0.02 ) pixelCol = blue;   

    // Output to screen
    fragColor = vec4(pixelCol,1.0);
}
