// --- fonts utilities --------------------------------------------------------
vec4 char(vec2 p, int c) { // char texture of ascii code c . From https://www.shadertoy.com/view/llySRh
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel0, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) );
}
//#define Char(p,c) char(p,c).x                                               // simple chars
#define Char(p,c) smoothstep( fsize/R.y, -fsize/R.y, \
                               char(p*mat2(1,-.2,0,1),c).w-.5        \
                              + .05*(texture(iChannel1,U*R.y*.5/256.).x-.5) ) // chalk effect

int CAPS=0;
#define low CAPS=32
#define caps CAPS=0
#define C(c) V.x--; O += Char(V,64+CAPS+c) * col;
#define N(c) V.x--; O += Char(V,48+c) * col;

#define fsize 12.
#define newline(dx) { U.y +=.15;  V = ( U - vec2(0,.5) ) * fsize; V.x += 2. + (dx+0.)*.8; }
#define A      V.x--; O += Char(V,97) * col;
#define B      V.x--; O += Char(V,98) * col;
#define EQ     V.x--; O += Char(V,61) * col;
#define PLUS   V.x--; O += Char(V,43) * col;
#define MINUS  V.x--; O += Char(V,45) * col;
#define TIMES  V.x--; O += Char(V,42) * col;
#define LEFT   V.x--; O += Char(V,40) * col;
#define RIGHT  V.x--; O += Char(V,41) * col;

#define c col = vec4(1,.8,.5,1);
#define b col = vec4(1);

void mainImage( out vec4 O, vec2 U )
{
    O -= O;
    vec4 col; b;
    vec2 V, R  = iResolution.xy;
	U = ( U - .5*R ) / R.y;

	float t = mod(iTime*.5, 17.);      // scroll
    t = floor(t) + min(2.*fract(t),1.);
    U.y += .4; if (t>2.) U.y -= (t-2.-clamp(t-5.,0.,1.)) *.1; 

    O += max(0.,.2*(texture(iChannel2,U*.25).r-.5));    // dirty board

    // equations
	newline()                   A EQ B
    if (t>=3.) { newline(2.5)   c A TIMES b A EQ c A TIMES b B }
    if (t>=4.) { newline(7.5)   A TIMES A c MINUS B TIMES B b  EQ   A TIMES B c MINUS B TIMES B b }
    if (t>=5. && t<6.) { newline(11.3)  LEFT A PLUS B RIGHT LEFT A MINUS B RIGHT   EQ   B LEFT A MINUS B RIGHT }
    if (t>=6.) { newline(11.3)  LEFT A PLUS B RIGHT c LEFT A MINUS B RIGHT b  EQ   B c LEFT A MINUS B RIGHT b }
    if (t>=7.) { newline(2.5)   A PLUS B    EQ   B }
    if (t>=8.) { newline(2.5)   c B b PLUS B    EQ   B }
    if (t>=9.) { newline()      c N(2) b   EQ  c N(1) b}

    O += (1.-O.a) * vec4(.15,.4,.35,1); // blend background 
}
