// Basic Circle + Mouse Control Example by Barakooda

//29/03/2017




//fitting by smallest axis function

vec2 smallerAspectStretch(vec2 uv,float aspect)
{    
    if (aspect <= 1.0) 
    {
    	uv.y *= aspect ;
        return uv;
    }


    else  
    {
        uv.x *= 1.0/aspect;
        return uv;
    }



}   


// circle function
vec4 circle (float dis,float radius)
{


    return vec4( step(dis,radius ) );

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //normalize pixel space.
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 mouse = iMouse.xy/iResolution.xy;

    //screen aspect ratio
    float aspect = iResolution.y/iResolution.x ;



    // change cordinates range to -1 to 1 

	uv = uv * 2.0 - 1.0;
	mouse = mouse * 2.0 - 1.0;

    uv = smallerAspectStretch(uv,aspect);

	// circle properties
	float radius = 0.1;

    // notice here to multiplie the smaller axis by aspect
  	vec2 position = vec2(mouse.x,mouse.y*aspect);

   //	ollj, 2017-Mar-29 upgrade:
   fragColor = circle (length(position-uv),radius);
    //old option
	//fragColor = circle (position,uv,radius); 


}
