//BASED ON: https://www.shadertoy.com/view/4ssSRS by XOR
//Based on: https://www.shadertoy.com/view/lsf3WH by IQ

#define PI 3.1415
#define Scale 0.8
#define height 0.3
#define hmax 0.8
#define type  1.
#define speed 0.4
float meuHash(float p){
 return fract(floor(p)*sin(p* 1213.232) * 2214.32);   
}

float meuNoise(float p){
    float ts[5];// = {meuHash(p-2.),meuHash(p-1.),meuHash(p),meuHash(p+1.),meuHash(p+2.)};
   ts[0] = meuHash(p-2.);
  ts[1] = meuHash(p-1.);
 ts[2] = meuHash(p-0.);
ts[3] = meuHash(p+1.);
ts[4] = meuHash(p+2.);

    return mix(mix(ts[0],ts[1],0.9),mix(ts[3],ts[4],0.1),0.5);



}




float noise(float n,float s,float res)
{
	float a = fract(sin(((floor((n)/s-0.5)*s)/res)*432.6326)*556.6426);
	float b = fract(sin(((floor((n)/s+0.5)*s)/res)*432.6326)*556.6426);
	return mix(a,b,smoothstep(0.0,1.0,+mod(n/s+0.5,1.0)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = fragCoord.xy / iResolution.xy;

	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);

    float h = (1. - cos( PI*(uv.y - 0.5)))*hmax;     


     uv.x += iTime*speed;


   float n1 = meuNoise(p.x +iTime/3.);

    float height2 = (noise(fragCoord.x+iTime*100.0,12.0,iResolution.x)+
					noise(fragCoord.x+iTime*150.0,20.0,iResolution.x)+
				    noise(fragCoord.x+iTime*320.0,235.0,iResolution.x)+
				    noise(fragCoord.x+iTime*500.0,910.0,iResolution.x))/4.0;

    height2 += n1/10.;
    if(h + height2/2.  < 0.95){
    if(uv.y > 0.5)
        fragColor =  texture(iChannel0, vec2( p.x, p.y + h*height2/2. ));
                             // vec4(height2,height2,height2,1.0);
     else
	    fragColor =  texture(iChannel0, vec2( p.x, p.y  - h*height2/2.));
                             // vec4(height2,height2,height2,1.0);

     // fragColor = );   
    }
    else
    {
    // fragColor = vec4(0.,1.,0.,1.);   
    }

}
