/*

Complex Gaussian based circular kernel.

Reproducing the 2 components kernel demonstrated here:
http://yehar.com/blog/?p=1495

See https://www.shadertoy.com/view/4dVBRK for the detail of one component.

-- 
Zavie

*/

vec2 complexGaussianKernel(vec2 xy, float a, float b)
{
    // Complex Gaussian kernel:
    // f(x,y) = exp((a + ib)*(x*x + y*y))

    float sqrDist = dot(xy, xy);
    float norm = exp(-a * sqrDist);
    float theta = b * sqrDist;
    return norm * vec2(cos(theta), sin(theta));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(fragCoord/iResolution.xy - 0.5) * vec2(1., iResolution.y/iResolution.x);

    vec2 component0 = complexGaussianKernel(uv, 0.886528, 5.268909);
    vec2 component1 = complexGaussianKernel(uv, 1.960518, 1.558213);

    float weightedSum = dot(vec4(component0, component1),
                            vec4(0.411259, -0.548794, 0.513282, 4.561110));

    vec3 color = vec3(weightedSum * 0.5+0.5);
    fragColor = vec4(color, 1.0);
}
