// subjective frame variant of https://shadertoy.com/view/4sKBzK


#define rot(a) mat2(cos( a + vec4(0,33,11,0)))   // From https://www.shadertoy.com/view/XlsyWX
#define hue(v) ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) ) // From https://www.shadertoy.com/view/ll2cDc


// --- return hexa cell center + dist to center. // From https://www.shadertoy.com/view/lsVBWw

vec2 Hcoords( vec2 U, out float d ) {
#define SQR3S2 0.866
    U.y /= SQR3S2;
    U.x -= .5*U.y;                           // global slant

    vec2 F = fract(U);                       // triangular barycentric coordinates
    bool u = F.x+F.y > 1.;       
    vec3 q = vec3(F, fract(1.-F.x-F.y));
    if (u) q = 1.-q;
    vec3   r = q - q.zxy;

    U = floor(U);                            // hexagonal center, d = dist to border
    if      ( r.x>0. && r.y<0. ) U += vec2(!u,u), d = min(r.x,-r.y);
    else if ( r.y>0. && r.z<0. ) U += vec2(u,!u), d = min(r.y,-r.z);
    else                         U += vec2(u)   , d = min(r.z,-r.x);

    return U;
}

#define blend(c,d) O = mix(O, vec4(c), smoothstep(.02,0.,d))
#define B(U,d,h)   length( U -(d)*D -(h)*H )   // Ball at coord(d,h) relative to (D,H)

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy, r = 400.*R/R.y / PIX;
    float L = T(1,0).w*k;                      // segments * steps ( = pos to queue )
    U *= 400./R.y;
    U /= PIX; 
    vec2 P0 = T(0,0).xy; U += P0-r*.5;
    float d;
    vec2 P = Hcoords(U * rot(-.2), d);
    O = ( smoothstep(.0,.01,d-.33)*.4 + sqrt(clamp(-5.*(d-.23),0.,1.))*.2 )
         * vec4(.7,.7,1,0)*.6;

    for (float i=0.; i<N0balls; i++)           // draw energy balls
        if ( T(i,1).z != -1.) {
            d = length( mod( U - T(i,1).xy+.5*r , r)-.5*r );
            vec2 r = rnd(i+.5);
            O +=  .01*(1.+r.x+.3*sin(6.3*r.y+13.*iTime)) / (d*d) * hue(i/10.);
        }

    vec2 D = V*D(T(0,0).z), H = vec2(-D.y,D.x);
    for (float i=L-k; i>=0.; i-=k) {          // draw worm body
        P = T(i,0).xy;
        d = length( U - P );
        blend(d, d-.37);
    }
    float h = T(0,0).w;
    d =       B( U-P, 1., .7 );               // draw worm eyes
    d = min(d,B( U-P, 1.,-.7 ));
    blend(1., d-.15);

    d =       B( U-P, 1.4, .7+h );
    d = min(d,B( U-P, 1.4,-.7+h ));
    blend(0., d-.08);
}

#define m .39/1.41                   // screen modulo margin

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    float i = U.x,                   // queued positions : 0 ... L-1 (head)
          j = U.y;
   if (j>1.) return; 

    vec2 R  = iResolution.xy, r = 400.*R/R.y / PIX;         

    if (iFrame==0) {                 // --- initial position
        if (j==0.) {
            if (i==0.) O.z = A0;     // head angle
            if (i==1.) O.w = N0segs; // start length
            float L = N0segs*k;      // segments * steps ( = pos to queue )   
            vec2 P0 = vec2(330,150)/PIX, D = D0, H = vec2(-D.y,D.x);
            i = (L-i)/k-1.;
            O.xy = mod( P0 + i * D + .3*H*sin(i/2.), r+.39/1.41 );
        }
        else O.zw = rnd(i)*r;        // energy balls
        return;
    }
                         // --- simulation
    O = T(i,j);                      // previous state
    vec2 P0 = T(0,0).xy;             // worm head

    if (j==0.) {
        if (i==0.) {                 // head motion
            vec2 M = iMouse.xy * 400./R.y / PIX + P0-r*.5;
            float a = 0.;
            if (iMouse.z>0.)         // mouse control turns head
                a = - 14./k * sign( cross2(normalize(M-O.xy),D(O.z)) ),
                O.z += a*dt;
            O.w = mix( O.w, .15*a, dt*3. );
            float L = T(1,0).w*k;    // segments * steps ( = pos to queue )
            i = (L-i)/k-1.;
            O.z += .1* sin(i/k/2.+5.*iTime)/5.;              // snake motion 
            O.xy += V/k * D(O.z);    // head move
        }
        else O.xy = T(i-1.,0).xy;    // segments (and inter-step) follows
        if (i==1.)  {                // if eat ball, length ++
            vec2 H = T(0,0).xy;
            for (float i=0.; i < N0balls; i++)
                if ( length(H - T(i,1).xy) < .3 ) O.w++;   // worm length += 1
        }
    }
    else {
        if (O.z==-1. && 1.-rnd(i+iTime).x<.001)  O.zw = rnd(i-iTime)*r; // new balls

        O.xy = O.zw - .06*cos(i+4.*iTime+vec2(33,0)); // energy balls
        P0 = mod(P0,r);
        if ( length( P0 - O.xy )< .3 ) O = vec4(-1);  // eaten
    }
}
