// Thanks MacSlow, XT95 and iq

#define STEPS        64
#define FAR         5.0
#define PI  acos( -1.0 )
#define TIME iTime * 0.3
#define WAV texture( iChannel0, vec2( 0.0, 0.05 ) ).x
#define TWA TIME + WAV
//#define       PROCEDURAL


float hash( float n )
{

    return fract( sin( n ) * 45843.349 );

}

// iq's

#ifdef PROCEDURAL

float noise( in vec3 x )
{

    vec3 p = floor( x );
    vec3 k = fract( x );

    k *= k * k * ( 3.0 - 2.0 * k );

    float n = p.x + p.y * 57.0 + p.z * 113.0; 

    float a = hash( n );
    float b = hash( n + 1.0 );
    float c = hash( n + 57.0 );
    float d = hash( n + 58.0 );

    float e = hash( n + 113.0 );
    float f = hash( n + 114.0 );
    float g = hash( n + 170.0 );
    float h = hash( n + 171.0 );

    float res = mix( mix( mix ( a, b, k.x ), mix( c, d, k.x ), k.y ),
                     mix( mix ( e, f, k.x ), mix( g, h, k.x ), k.y ),
                     k.z
    				 );

    return res;

}

#else

float noise( in vec3 x )
{


    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
    vec2 rg = textureLod( iChannel1, (uv+ 0.5)/256.0, 0.0 ).yx;
    return mix( rg.x, rg.y, f.z );

}

#endif

float fbm( in vec3 p )
{

    float f = 0.0;
    f += 0.5000 * noise( p ); p *= 2.02;
    f += 0.2500 * noise( p ); p *= 2.03;
    f += 0.1250 * noise( p ); p *= 2.01;
    f += 0.0625 * noise( p );
    f += 0.0125 * noise( p );
    return f / 0.9375;

}

float map( vec3 p )
{

    return 0.5 - length( p ) * fbm( p + TWA );

}

float ray( vec3 ro, vec3 rd, out float den )
{

    float t = 0.0, maxD = 0.0; den = 0.0; 

    for( int i = 0; i < STEPS; ++i )
    {

        vec3 p = ro + rd * t;
        den = map( p );
        maxD = maxD < den ? den : maxD;
        if( maxD > 1.0 || t > FAR ) break;
        t += 0.1;

    }

    den = maxD;
    return t;

}    

vec3 shad( vec3 ro, vec3 rd )
{

    float den = 0.0;
    float t = ray( ro, rd, den );

    vec3 p = ro + rd * t;

    vec3 col = mix( vec3( 1.2, 0.1, 0.5 ), vec3( 0.1, 0.0, 0.7 ), map( p ) );


    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( -iResolution.xy + 2.0 * fragCoord ) / iResolution.y;
    vec2 mou = iMouse.xy / iResolution.xy;

    vec3 ro = 4.0 * vec3( sin( mou.x * 2.0 * PI ), mou.y, cos( -mou.x * 2.0 * PI ) );
    vec3 ww = normalize( vec3( 0.0 ) - ro );
    vec3 uu = normalize( cross( vec3( 0.0, 1.0, 0.0 ), ww ) );
    vec3 vv = normalize( cross( ww, uu ) );
    vec3 rd = normalize( uv.x * uu + uv.y * vv + 1.5 * ww );
    //vec3 rd = normalize( vec3( uv, -1.0 ) );

    float d = 0.0;
    float den = 0.0;
    float t = ray( ro, rd, den );

    // Time varying pixel color
    //vec3 col = d < EPS ? shad( ro, rd ) : mix( vec3( 1.0 ), vec3( 0.2, 0.5, 0.8 ), uv.y );

    // https://stackoverflow.com/questions/50662921/volumetrics-in-a-pixel-shader

    /*
	vec3 col = mix(mix( vec3( 1.0 ), vec3( 0.2, 0.5, 0.8 ), uv.y ), shad( ro, rd ), step( d, EPS ) * den );
    */

	vec3 bkCol = mix( vec3( 0.8 ), vec3( 0.2, 0.5, 0.8 ), uv.y );
	vec3 col = mix( bkCol, shad( ro, rd ), den );


    //vec3 col = d < EPS ? shad( ro, rd ) : mix( vec3( 1.0 ), vec3( 0.2, 0.5, 0.8 ), uv.y );;

	col *= sqrt( col );    

    // Output to screen
    fragColor = vec4(col,1.0);
}
