#define pi 3.1415
#define from vec3(253./255.,27./255.,32./255.)
#define to vec3(198./255.,48./255.,249./255.)

float gradient(float t){
	return .5 + .5*sin(t);
}

float luminance(float t){
	return-.1*cos(t);
}

vec3 colour(float t){
    return from + gradient(t)*(to-from) + luminance(t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.0*(mod(fragCoord, iResolution.xy/2.) - .25*iResolution.xy)/iResolution.y;
    vec2 polar = vec2(length(uv), atan(uv.x, uv.y));
    fragColor.xyz = smoothstep(0.0, 0.1, polar.x)*colour(10.*polar.y + iTime/2. + 10.*polar.x );
}
