#define zoom 3.
//#define showGrid
#define showResult


#define S(v) smoothstep(pixels,0.,v)
vec2 distort(vec2 uv){
	float ang=atan(uv.x,uv.y)/4.;
	return uv*mat2(cos(ang),-sin(ang),sin(ang),cos(ang));
    //return vec2(ang/3.14,1./length(uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 R=iResolution.xy,
	uv=zoom*(2.*fragCoord-R)/R.y,
    m=zoom*(2.*iMouse.xy-R)/R.y;


    float pixels=2./iResolution.y*(zoom);
    vec3 col;
    #ifdef showGrid
    col+=vec3(0.3*S(abs(uv.x)));
    col+=0.3*S(abs(uv.y));
	col+=0.2*S(abs(fract(abs(uv.x)-0.5)-0.5));
    col+=0.2*S(abs(fract(abs(uv.y)-0.5)-0.5));
    col+=0.1*S(abs(fract(abs(uv.x)*10.-0.5)-0.5));
    col+=0.1*S(abs(fract(abs(uv.y)*10.-0.5)-0.5));
    #endif

   	uv=distort(uv);

    #ifdef showResult
    col+=vec3(1,1,0)*S(abs(uv.x));
    col+=vec3(0,1,1)*S(abs(uv.y));
	col+=vec3(1,0,0)*S(abs(fract(abs(uv.x)-0.5)-0.5));
    col+=vec3(0,0,1)*S(abs(fract(abs(uv.y)-0.5)-0.5));
    col+=0.3*S(abs(fract(abs(uv.x)*10.-0.5)-0.5)/10.);
    col+=0.3*S(abs(fract(abs(uv.y)*10.-0.5)-0.5)/10.);
	#ifdef showGrid
	col/=2.;
    #endif
    #endif

    if(iMouse.z>1.)col+=vec3(0,1,0)*S(distance(uv,m)-0.02);

    fragColor = vec4(col,1.0);
}
