float sRectangle(vec2 p){
  return smoothstep(0.0,0.01,p.y);
}

float sCircle(vec2 p){
  return smoothstep(0.99,1.0,p.x*p.x+p.y*p.y);
}

float sAnnulusSmooth(vec2 p, float width, float smoothie){
  float s1 = 1.0-smoothie;
  float s2 = 1.0+smoothie;
  return smoothstep(width*s1,width*s2,p.x*p.x+p.y*p.y)*(1.0-smoothstep(s1,s2,p.x*p.x+p.y*p.y));
}

float sHairComb(vec2 p, float subdiv){
  vec2 peval = p;
  float pattern = step(0.0,sin(3.14159*(peval.x*subdiv)));
  peval.x = floor(peval.x*subdiv)/subdiv;

  if(pattern>0.0){
    peval.y = 0.3*sin(17.0*peval.x+10.0*iTime) - peval.y;
    peval.y += 4.0*exp(-0.02*peval.x*peval.x);
  }else{
    peval.y = 0.2*sin(233.0*peval.x+7.0*iTime) - peval.y-0.5;
    peval.y += 2.8*exp(-0.02*peval.x*peval.x);
  }

  float f = sRectangle(peval);

  peval.x=p.x-peval.x-1.0/(2.0*subdiv);

  if(pattern>0.0){
    f = max(f,1.0-sCircle(2.0*subdiv*peval));
  }else{
  f = f-(1.0-sCircle(2.0*subdiv*peval));
  }

  return step(1.0,f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

  vec2 p = fragCoord.xy/iResolution.xy;
  p-=0.5;
  p.x *= iResolution.x/iResolution.y;

  float angle = -0.5;
  float scale = 60.0;
  mat2 m = mat2(cos(angle),-sin(angle),sin(angle),cos(angle));

  vec2 pp;
  float f;

  vec4 col = vec4(0.98,0.63,0.49,1.0);

  pp=m*p;
  pp = pp*scale;
  pp.y-=6.0;
  f = sHairComb(pp,2.0);
  col = mix(col,vec4(0.97,0.38,0.46,1.0),f);

  pp=m*p;
  pp = pp*scale;
  pp.y-=3.0;
  f = sHairComb(pp,2.0);
  col = mix(col,vec4(0.97,0.17,0.48,1.0),f);

  pp=m*p;
  pp = pp*scale;
  pp.y+=4.0;
  f = sHairComb(pp,2.0);
  col = mix(col,vec4(0.91,0.098,0.48,1.0),f);

  pp=m*p;
  pp = pp*scale;
  pp.y+=7.0;
  f = sHairComb(pp,2.0);
  col = mix(col,vec4(0.83,0.1,0.47,1.0),f);

  pp = p*5.0;
  f=sCircle(pp);
  col = mix(col,vec4(0.26,0.24,0.4,1.0),f);

  pp = p*4.9;
  f=f*sAnnulusSmooth(pp,0.9,0.2+0.05*sin(10.0*iTime));
  col = mix(col,vec4(0.83,0.1,0.47,1.0),f);

  //Debug
  //col = f*vec4(1.0,1.0,1.0,1.0);

  fragColor = col;

}
