void mainImage( out vec4 O, vec2 u )
{
    vec2 uv = u / iResolution.xy;
    float t = iTime*.02;

    vec4  a = sin( (uv.x + t) * vec4( 350.13, 2.5, 10, .3 ) ),
          b = sin( (uv.x - t) * vec4( 150.3, .5, 20, 11.3 ) );
    int   y = int(10.*uv.y)-5;
    float k = y<4 ?  a[y]   : a.a + a.b - a.g + a.r,
          j = y<0 ?  b[y+4] : b.a + b.b - b.g * b.r;

    O += abs(k*j) -O;
}
