void mainImage( out vec4 O,  vec2 U )
{
    U -= .5;
     float y = 5.*U.y/iResolution.y;
    // strangely, compilation errors show up only if another compil error (e.g. redef). 
    // maybe overflow = warning ?
     float  // if const float is used, then behave at preproc like #defines
         MAXFLOAT       = 3.402823e+38,
         MAXFLOATb      = 3.4028235677973e+38,  // max not triggering overflow at compil
         //MAXFLOATplus1  = 3.402824e+38,       // float overflow at compilation on linux
         MAXFLOATplus1  = MAXFLOAT     + 1e32,  // solve at execution: denormalized floats
         MAXFLOATplus1b = 3.402823e+38 + 1e32,  // solve at compilation: overflow (linux)
         MAXFLOATdiv10  = 3.402824e+37;

    if (fract(y)<.02) { O=vec4(1,0,0,1); return; }

    O =   y<1. ? vec4( mod(0./0., U.x))
        : y<2. ? vec4( mod(MAXFLOAT, U.x))
        : y<3. ? vec4( mod(MAXFLOATplus1, U.x))
        : y<4. ? vec4( mod(MAXFLOATplus1b, U.x))
        :        vec4( mod(MAXFLOATdiv10*10., U.x)); // NaN  (+1 keeps black)

}
