/**  // Fab factored version: 312 chars

// band mask =   main colored band (1/3)  - small black band (1/3/30)
#define B(U,n) step(mod(U,3.), R/R) * step(.5, abs( 30.*abs( mod(U-.5,3.) -1.5) - 45.+n ))

void mainImage( out vec4 O,  vec2 U )
{
    float t = iDate.w*.1, C=cos(t), S=sin(t);
    vec2 R = iResolution.xy, s;
	U = (U-.5*R)/R.y * mat2(C,S,-S,C) *5.;                     // cloth coordinates
    s = .5 + .5* sin( 50.*(U.x+U.y) ) *vec2(1,-1);             // thin strip pattern

    O =    vec4(0,.5,0,1) * dot( s, B(U,     0.) )             // green bands
         + vec4(0,0,1, 1) * dot( s, B(U+1.5, 3.) );            // blue bands
}

