/**   // Fab initial readable version -- 467 chars  

void mainImage( out vec4 O,  vec2 U )
{
    O -= O;
    float t = iDate.w*.1, C=cos(t), S=sin(t);
    vec2 R = iResolution.xy;
	U = (U-.5*R)/R.y * mat2(C,S,-S,C) *5.;                     // cloth coordinates

    vec2 s = .5 + .5* sin( 50.*(U.x+U.y) ) *vec2(1,-1),        // thin strip pattern
    bU = floor(mod(U,3.)), bU2 = floor(30.*mod(U-.5,3.));      // colored band, black band
    if (bU.x==0. && bU2.x>0.) O += vec4(0,.5,0,1)*s.x;
    if (bU.y==0. && bU2.y>0.) O += vec4(0,.5,0,1)*s.y;

    U += 1.5;
    bU = floor(mod(U,3.)), bU2 = abs(floor(30.*mod(U-.5,3.))-45.) - 42.;
    if (bU.x==0. && bU2.x!=0.) O += vec4(0,0,1,1)*s.x;
    if (bU.y==0. && bU2.y!=0.) O += vec4(0,0,1,1)*s.y;   
}

