const float PI = 3.14159265358979;
const float TWO_PI = 2. * PI;


 float circle(vec2 circle_pos, vec2 coord, float circ_rad) {
	 float circ_blur = 1.0;
	 float dist = distance(circle_pos, coord);
	 return smoothstep(circ_rad + circ_blur, circ_rad - circ_blur, dist); 
 }

 void mainImage( out vec4 fragColor, in vec2 fragCoord ){
     vec2 center = iResolution.xy * 0.5;

     vec3 color1 = vec3(113., 27., 134.) /255.;
     vec3 color2 = vec3(255., 109., 40.) /255.;

     float circles;
     float time = iTime * 2. * PI / 15.;
     float rayon = 100.* abs(sin(time) / 5. + 1.5);
     float maxIteration = rayon / 10.;
     float circleRadius = rayon / 10.;

     const float numDisks = 32. ; 

     for (float i = 0.0; i < 6.; i++) {
         for (float j = 0.0; j < numDisks; j++) {
             vec2 ptSurCercle = center + vec2(cos(TWO_PI/ numDisks * j), sin(TWO_PI / numDisks * j)) * ((rayon - rayon* i/rayon * 27. ))  ;
             circles += circle( ptSurCercle, fragCoord.xy, circleRadius - circleRadius * i * 3. / circleRadius);
         }
     }     



     fragColor = mix(vec4(color1, 1.), vec4(color2, 1.), circles );
 }
