float udRoundBox( vec2 p, vec2 b, float r )
{
    return length(max(abs(p)-b+r,0.0))-r;
}

float rect(vec2 p, float sx, float sy, float a, float b)
{
   if(p.x>sx && p.x<a && p.y>sy && p.y<b) 
      return 1.0;
   else 
      return 0.0;
}
float rect2( vec2 p, vec2 b )
{
    return length(max(abs(p*0.8)-b,0.0));//-r;
}


float tri( vec2 p, vec2 b )
