float triangleDist(vec2 p, float width, float height)
{
	vec2 n = normalize(vec2(height, width / 2.0));
	return max(	abs(p).x*n.x + p.y*n.y - (height*n.y), -p.y);
}
vec2 rotateCW(vec2 p, float a)
{
	mat2 m = mat2(cos(a), -sin(a), sin(a), cos(a));
	return p * m;
}

// https://stackoverflow.com/questions/43970170/bordered-rounded-rectangle-in-glsl
// https://www.shadertoy.com/view/MssyRN
float roundRect( vec2 p, vec2 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

//https://www.shadertoy.com/view/MssyRN
float ring(vec2 r, vec2 pos, float radius, float thick)
{
  return mix(1.0, 0.0, smoothstep(thick, thick + 0.002, abs(length(r-pos) - radius)));
}

float circle(vec2 p, vec2 r, float R)
{
    return (length(p-r)-R);		// transition
}

