// old one
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = ( fragCoord.xy / iResolution.xy );
	p.y = 1.0-p.y;	
	vec3 color = vec3(0);

	for(int i = 1; i<=60; i++){
		vec3 pt = vec3(0);
		pt.x = float(i)/60.0*1.0;
		pt.y = mod((iTime*1.0)+sin(float(i))*2.0,1.2);
		pt.z = 0.04+abs(sin(float(i))*0.04);
		if (pt.y >= 1.0) {
          pt.y = 1.0 - pt.y * 0.05;
          pt.z = 0.02;
        }

		if((p.x>pt.x && p.x<=pt.x+0.001)&&(p.y>pt.y && p.y<=pt.y+pt.z)){
			color = vec3(1);
		}
	}

	fragColor = vec4( color, 1.0 );

}
