void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float cameraDistance = 20.;
    float linesWeight = 2.0;

    float uvXRatio = uv.x * cameraDistance;
    float uvYRatio = uv.y * cameraDistance;

    float verticalSpeed = iTime * 2.;
    float rotateAmount = linesWeight / 5.;

    //float thingOffset = 0.2 * sin(iTime);
    float rotation = 0.001 * (iMouse.x + 1.) + rotateAmount;

    float uvMultiplier = (verticalSpeed / uvXRatio + rotation);

    if(uv.x > 0.75) {
        uvMultiplier += rotateAmount;

    } else if(uv.x > 0.5) {
        uvMultiplier -= rotateAmount * 3.;

    } else if(uv.x > 0.25) {
        uvMultiplier += rotateAmount;

    } else {
       uvMultiplier -= rotateAmount * 2.;
    }


    // pixel color
    vec3 col = vec3(1.0, 1.0, 1.0);

    // mods
    float modX = mod(uvXRatio, 2.0);
    float modY = mod(uvYRatio, 2.0);


    //old: float uvRatio = mod(uvMultiplier * uvXRatio + uvYRatio, linesWeight);
    float uvRatio = sin( 6.28*( uvMultiplier * uvXRatio + uvYRatio) / linesWeight);

    //old: float color = step(uvRatio, 1.0);
    float color = 9.*uvRatio;

    col = vec3(color);   

    // Output to screen
    fragColor = vec4(col, color);
}

