#define AA 2
#define time iTime

vec2 cmplxMult(vec2 a, vec2 b)
{
    return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 p = -1.0 + 2.0 * (fragCoord.xy+vec2(float(m),float(n))/float(AA)) / iResolution.xy;
        p.x *= iResolution.x/iResolution.y;

        float t = smoothstep(0.0,1.0,((cos(time*.1)+1.0)/2.0));

        float zoom = 1.0/(t*127.0+1.0);

        vec2 um = iMouse.xy==vec2(0)?vec2(0):(iMouse.xy / iResolution.xy)-0.5;
        um = vec2(um.y,-um.x);
		um.x *= iResolution.x/iResolution.y;

        vec2 c = vec2(-p.y-0.5,p.x);
        c -= t*vec2(-2.89,3.33)*16.0;
        c *= zoom;
        c += um*0.3;
        vec2 orig = vec2(0.5,0.0);
        float obs = dot(c-orig,c-orig);
        vec2 z = vec2(0.0);
        vec2 zl = c;
        vec2 w = c;
    	int k=0;
        for(int i=0; i<100; i++ )
    	{
            z = c + vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y );
            if(obs>=0.5 || i>=4)
            w = 0.985*w+0.015*cmplxMult(z,zl)/dot(z,zl);
            zl=z;
            if( dot(z,z)>1e12 ){k=i;break;}
        }

        float f = float(k)/55.0;

        if(dot(z,z)>1024.0)
        	col +=vec3(0.9,0.0,0.0)*f+vec3(0.0,abs(w.x),abs(w.y))*0.9;
    }
    col /= float(AA*AA);
    fragColor = vec4( col, 1.0 );
}
