vec4 stars(vec2 uv)
{
    float density = 2.0*PI;
    uv *= density;
    float s = floor(uv.x)*1234.1234+floor(uv.y)*123.123;
    vec2 p = floor(uv)+saw(floor(uv)+iTime+s)*.5+.25;

