              + cos(1.0-(cos(theta2)*cos(theta2)+cos(theta2+PI/2.)*cos(theta2+PI/2.))/2.0);

    plant = plant*PI+arms+ 
                           atan(last_uv.x,last_uv.y)
                           ;
    vec4 color = vec4(flux(plant)+
                      (1.0-smoothstep(0.0, .5/5.0, saw(plant-time))), 1.0)
        *(1.0-smoothstep(4.5/5.0, 1.0, saw(plant-time)));

