void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    for(int i = 0; i < parts; i++)
    {
        ivec2 index = ivec2(i/int(iResolution.y),i%int(iResolution.y));
		vec4 dat = texelFetch(iChannel0, index,0);
        col += .3/(length(fragCoord-dat.xy))*(.5+.5*sin(vec3(.01,.014,.018)*dat.xxy+iTime));
        col += .3/(length(fragCoord-dat.zw))*(.5+.5*sin(vec3(.02,.014,.018)*dat.zww+iTime));
    }

	fragColor = pow(vec4(col,0),vec4(2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if(iFrame < 1)
    {
        vec2 pos = iResolution.xy/2. + 4.*sin(fragCoord.yx)*fragCoord.xy;

        fragColor = vec4(pos,pos+2.4);

		//fragColor = 10000.*sin(vec4(4.234,24.35,2312.232,432.2)*(+vec4(34.,463.,3.,3.))+iTime);
    }
    else if (fragCoord.x < 4.)
    {
    	vec4 pos = texelFetch(iChannel0, ivec2(fragCoord),0);
        vec4 vel;

        if((iFrame%int(parts)==int(fragCoord.y)) && iMouse.z > .5)
            if(int(iTime*9999.)%2==0)
        		pos.xy = iMouse.xy;
            else
        		pos.zw = iMouse.xy;
    	vel = 1.5*sin(pos.yxwz/60.+iTime);

        pos += vel;

        pos = mod(pos,iResolution.xyxy);
        fragColor = pos;
    }
}
