mat3 RotX(float a){a = radians(a); return mat3(1.,0.,0.,0.,cos(a),-sin(a),0.,sin(a),cos(a));}
mat3 RotY(float a){a = radians(a); return mat3(cos(a),0.,sin(a),0.,1.,0.,-sin(a),0.,cos(a));}
mat3 RotZ(float a){a = radians(a); return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec2 path(float t, vec2 s)
{
	vec2 p = vec2(cos(t*0.2), sin(t*-0.2)) * s * 2.;
	t *= 0.1;
	p *= mat2(cos(t),-sin(t),sin(t), cos(t));
	return p;
}

// base torus => length(vec2(length(p.xy)-1.,p.z))-0.2

float getTutu(vec3 p)
{
    p.z = abs(fract(p.z)-0.5); // repeat along z
	float x = length(p.xy)-0.62; // x side => circle size
	float y = max(abs(p.y),abs(p.z))-0.5; // y side => square size
	float z = max(abs(p.x),abs(p.z))-0.5; // z side => square size
	return length(vec3(x,y,z))-0.15; // thick
}

float df(vec3 p)
{
    p.xy -= path(p.z,vec2(0.2,0.2));
    float tutu = getTutu(p);

	p.xy -= path(p.z,vec2(-1.5,0.8));
    float tutu1 = getTutu(p);

	p.xy -= path(p.z,vec2(0.8,1.5));
	float tutu2 = getTutu(p);

    return min(min(tutu,tutu1),tutu2);
}

vec3 nor(vec3 p, float prec)
{
	vec3 e = vec3(prec,0,0);
	return normalize(vec3(
		df(p+e.xyz)-df(p-e.xyz),
		df(p+e.yxz)-df(p-e.yxz),
		df(p+e.yzx)-df(p-e.yzx)));
}

vec3 vecPath(vec3 p, vec2 gs)
{
    p.xy += path(p.z,vec2(0.2,0.2)); // tutu 0
    p.xy += path(p.z,vec2(-1.5,0.8)); // tutu 1
	//p.xy += path(p.z,vec2(0.8,1.5)); // tutu 2
    return p;
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    vec2 gc = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 gs = gc / si;

	vec2 uv = (gc+gc-si)/si.y;

    float t = iTime;
	float an = t * 5.;

    float prec = .1;
	vec3 ro = vecPath(vec3(0,0,an), gs);
   	vec3 cv = vecPath(vec3(0,0,an + prec), gs);
	vec3 cu = normalize(vecPath(vec3(0,prec,0), gs));

	vec3 z = normalize(cv-ro);
	vec3 x = (cross(cu,z));
	vec3 y = (cross(z,x));
    float fov = length(x) * 0.75;
	vec3 rd = normalize(fov * (uv.x * x + uv.y * y) + z);

	float d = 0., s = 1.;
	for (int i=0; i< 200; i++)
	{
		if (log(d*d/s/1e5)>0.) break;
		s = df(ro+rd*d);
		d += s * 0.5;
    }

    if (d<30.)														
    {
        vec3 n = nor(ro+rd*d, 0.1);
        fragColor= texture(iChannel0, n);
    }      

    fragColor.a += d;
}
