// Zoom scale and position to center zoom in at
#define zoom 1.0
#define cx 0.0
#define cy 0.0

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Variables
	float scale = 1.0 / zoom; 
    float ax, ay;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Change perspectives w/ zoom
    ax = cx + ((uv.x * 3.5) - 2.5) * scale;
    ay = cy + ((uv.y * 2.0) - 1.0) * scale;
    vec2 c = vec2 (ax, ay);

    // Set up calculation (a = real, b = imaginary)
    vec2 z = vec2 (ax, ay);
    int i;

    // Actual math of mandelbrot set
    for (i = 0; !(i > 4096 || dot (z, z) > 16.0); i++) 
    {
        // z = z^2 + c
        z = vec2 ((z.x * z.x) - (z.y * z.y), 2.0 * z.x * z.y) + c;
    }

    float color;

    // If part of set, make it black, if not, divide by 255 for colored version
    if (i > 4096)
    {
        color = 0.0;
    } 
    else
    {
		color = float (i) * 0.0625;
    }

    fragColor = vec4 (color, color, color, 1.0);
}
