#define S(x,y,l) smoothstep(e,-e, length(U+vec2(x,y))-l)

void mainImage( out vec4 O, vec2 U )
{
	vec2  R = iResolution.xy, V;
          U = (U+U-R)/R.y * 2.; 
          V = ceil(U+vec2(.5,0));
    float e = 10./R.y, l=U.y, 
          t = iTime;
    O -= O; 
    if (abs(U.x)>1.5) { O+=.3; return;}
    U += vec2(.5,0.);

    if (l > 1.) {                         // top area
        U += U;
        O += 1.-S(-1,-3.1,.6);
        O -= S(1,-2,.3) + S(-1,-2,.3) + S(-3,-2,.3);
        return; 
    }

    //       tiles              rotation
    U = 2.*fract(U)-1.;
    if (fract(t/acos(-1.))<.5) 
        U *= mat2( sin( t*(2.*mod(V.x+V.y,2.)-1.) + 1.57*vec4(1,2,0,1)) );

	O +=   S(0,0,.9) - S(0,0,.3);         // white disk + central dot

	O -=   S(-1,0,.3) + S(1,0,.3)         // rotating dots
         + S(0,-1,.3) + S(0,1,.3);
}
