// Based on the shader Ellipse - Distance of iq : https://www.shadertoy.com/view/4sS3zz

//* 237 c by FabriceNeyret2
void mainImage( out vec4 O,  vec2 U )
{
    float d =  length( U += U - (O.xy=iResolution.xy) ) / O.y 
             - mix( 3.5, .5, cos( fract( atan(U.x, U.y)*.95 )-.5 ) );

    O =   1. - sign(d)*vec4(.1,.4,.7,0) ;

	O *=   ( 1. - exp( d = -abs(d+d) )  )
	     * ( .8 + .2*cos( iDate.w/.1 -60.*d ) );

    O += smoothstep(.04,0.,-d) ;
   // O = mix( O, vec4(1), smoothstep(.04,0.,d) );
}/**/

