#define RAIN_DENSITY .002      // density of drop
#define BRIGTHNESS  .03        // raindrop brightness contrast
#define BLUR_LENGTH 30.        // max length of raindrop blured line
#define SPEED 500.

#define rnd(p,s)   fract(sin( ((p)+.01*(s)) *12.9898) * 43758.5453)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U -= .5;
    O = vec4(.5);

    float Ny = RAIN_DENSITY * R.y;            // number of drop per column
    for (float i=0.; i<=floor(Ny); i++) {     // to deal with more than one drop per column
        float y = floor( mod( rnd(U.x,2.*i)*R.y -SPEED*iTime, R.y) ); // drop altitude
        if ( rnd(U.x,2.*i+1.) < (Ny-i) && abs( U.y - y) < BLUR_LENGTH*U.x/R.x )
            O += BRIGTHNESS; //  / (U.x/R.x); // variant: keep total drop brightness. attention: saturated on the left 5% 
    }

    O = sqrt(O);                              // gamma correction
}
