float pmodfmFloat(float u,float n,float m,float f,float e){
 m=floor((m)+.5);        //pmodfm() step <-> offset to center of a tile
 u=u-m;n=n-m;            //pmodfM() step <-> offset by M
 float a=floor((u)+.5);              //pmodfq() [a] parameter is tile-centric [u]
 float b=(n)/(max(f,1.+(e))-1.);     //pmodfq() [b] parameter is modified [e]
 a=(fract(((a)/(b))+.5)-.5-(e))*(b); ///pmodfe(a,b,e); (woth 0.5 offset)
 return floor(a+.5);                 //undo offset to center of a tile
 ;}

