float bassSynth(int note, float time){    
    const float noteLength = 0.5;    
    if (time > noteLength) return 0.0;    
    float baseFreq = 6.2831*time*noteFreq(note);    
    float fadeIn = 1.0-pow(1.0-(time/noteLength),32.0);
    float fadeOut = 1.0-pow(time/noteLength,8.0);
     return (
        sin(baseFreq*pow(2.0,-6.0))*1.5 +
        sin(baseFreq*pow(2.0,-5.0))*0.5 + 
        sin(baseFreq*pow(2.0,-4.0))*0.05
    )*fadeIn*fadeOut;}

float krakenBass(float songTime){    
    // this redundancy isn't great but whatev
    float beatNum = songTime/beatLength;
    float beatStep = mod(floor(beatNum),8.0);//DIFFERENT BAR LENGTH FOR BASSLINE
    float barStep = mod(floor(beatNum/8.0),4.0);
    if ( beatStep == 3.0 && barStep < 2.0) return 0.0;
    if ( beatStep == 6.0 && barStep < 3.0) return 0.0;
    if ( beatStep == 7.0 && barStep == 3.0) return 0.0;    
    int bassNote = (mod(floor(beatNum/32.0),2.0) == 0.0) ? 8 : 4 ;    
    return bassSynth(bassNote, mod(beatNum,1.0));}


float wailSynth(int note, float time){    
    const float noteEnd = 32.0;
    const float noteStart = 4.0;    
    float noteFrac = max(0.0,min(1.0,(time-noteStart)/(noteEnd-noteStart)));
    float fadeIn = pow(noteFrac,2.0);
    float fadeOut = 1.0-(pow(noteFrac,2.0));    
    float baseFreq = 6.2831*time*noteFreq(note)*0.25;
    baseFreq += sin(time*4.5)*3.0;    
    return (
        sin(baseFreq)+
        sin(baseFreq*3.0)+
        sin(baseFreq*4.0)*0.75
    )*fadeIn*fadeOut*0.5;}

float krakenWail(float songTime){    
    float beatNum = songTime/beatLength;    
    int wailNote = (mod(floor(beatNum/32.0),2.0) == 0.0) ? 2 : 3;    
    return wailSynth(wailNote, mod(beatNum,32.0));    }




