//bubble noise  //n += note(C4, m, 0.0, 2.0, time);
float noteBubble(float hz, int type, float s, float d, float t) {
    float f=0.4;
    s *= f;
    d *= f;
    if(t >= s && t <= s+d) {
        t -= s;
        float rt = t;
		t *= CON;
    	if(type == 1) {
            rt = pow(rt+1.0,10.0);
            if(rt < 1.0) {
                rt = 1.0;
            }
    		return (sin(t*hz)/rt);
        } else if(type == 2) {
            float n = sin(t*hz);
            n *= fract(rt*hz);
            return n;
        } else if(type == 3) {
            t += sin(t*5.0)*0.005;
            float n = sin(t*hz);
            return n;
        } else if(type == 4) {
            t *= rt*4.0+0.5;
        	float n = sin(t*hz);
            rt = pow(rt*4.0+0.5,20.0);
            if(rt < 1.0) {
                rt = 1.0;
            }
            return n/rt;
        }}}//https://www.shadertoy.com/view/XdScDy




