// reproducing http://twistedsifter.com/2016/11/best-illusions-of-2016-winners/
// (the rest in other shaders)

void mainImage( out vec4 O,  vec2 U )
{
	vec2 R = iResolution.xy;
    U = (U+U - R) / R.y;
    float l = length(2.*abs(U)-1.);

    if ( mod(iDate.w,2.) < 1. )
		O =   l < .2 ? vec4(.6,.9,.5,1)        // collored rings
    	    : l < .4 ? vec4(.5,.85,.85,1)
    	    : l < .6 ? vec4(.8,.65,.8,1)
    	    : l < .8 ? vec4(.9,.6,.6,1)      
    	    :          vec4(.8);   
    else if ( mod(iDate.w,4.) < 2. ) {
    	if (U.y*U.x>0.) l += .2;               // grey bubbles
    	if (U.x>0.)     l += .4;
    	l -= .75;
    	O += .8 -.1*max(1.-8e2*l*l,0.) -O;
    }
    else O += .8-O;

    U = abs(U*R.y);                            // cross
    U = U.x>U.y ? U.yx : U;
    if (U.x<2. && U.y<20.) O-=.3;
}
