#define intensity 1.0
#define colorswap 1.0

// Rand one-liner
float rand(vec2 co)
{
	return fract (sin (dot (co.xy, vec2 (12.9898, 78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float f = max (0.0, rand (uv * iTime) - rand (vec2 (0, uv.x * iTime)));
    vec4 c = vec4 (0.0, f, 0.0, f);

    // Time varying pixel color
    vec4 col = vec4 (0.5 + 0.5 * cos (iTime+uv.xyx + vec3 (0.0, 2.0, 4.0)), 1.0) * c;

    // Get color swap
    float swap2 = (0.5 - abs(0.5 - colorswap)) * 2.0;
    float swap1 = colorswap * (1.0 - swap2);

    // Swap colors?
    col.r = col.r * (1.0 - swap2) + (col.g * swap2);
    col.g = col.g * (1.0 - swap2) + (col.b * swap2);
    col.b = col.b * (1.0 - swap2) + (col.g * swap2);

    col.r = col.r * (1.0 - swap1) + (col.b * swap1);
    col.b = col.b * (1.0 - swap1) + (col.r * swap1);

    // Output to screen
    fragColor = col * (1.0 - intensity * 2.0) + (c * intensity * 2.0);
}
