#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching code stolen from TheArtOfCode

// Stolen from BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(80. - 70. * tanh(0.75 * iTime * iTime), 5. * thc(1., 0.81 * iTime), 5. * ths(1., iTime));
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
  //  p.x += 1.;
    p.y += 0.25 * thc(5., 1.25 * length(p) - iTime);;
    p.x += 0.5 * cos(0.5 * p.y + iTime);
    float y = fract(p.y) - 0.5;
    float d = length(vec2(p.x, y)) - 0.25 + 0.25 * thc(1., 4. * abs(p.z));//sdBox(p, vec3(1.)) - 0.1;

    float y2 = fract(p.y + 0.5) - 0.5;
    float d2 = length(vec2(p.x, y2)) - 0.25 + 0.25 * thc(1., 4. * abs(p.z) + pi);

    d = min(d, d2);

    return 0.75 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 bg(vec3 rd, vec3 p) {
    return 0.5 + 0.5 * thc(4., 0.5 *length(p) + 0.125 * pi * h21(p.xz) + rd.y - 0.5 *  iTime) * vec3(1);//abs(rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.5 + 0.75 * thc(4., 0.25 * log(length(uv)) - 0.5 * iTime));
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    vec3 p = ro + rd * d;

    if(d<MAX_DIST) {        
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);// * pal(length(p) *10., vec3(0,1,2)/3.);;

        float pw = 5.;
        float fres = 1.-pow(abs(1.25 + 0.25 * cos(10. * p.y + iTime) +dot(rd, -n)), pw);
        float fres2 = pow(abs(0.+dot(rdIn, rd)), 100.);

        vec3 fog = vec3(1.-exp(-0.3 * dIn));
        //col *= 0.5 + 0.5 * n.y;
        col += mix(vec3(0.25), pal(0.1 * iTime - length(p) * 0.15, vec3(0,1,2)/3.), cos(fres * 0.025) * 0.5 + 0.5);
        col = mix(vec3(1), col, 0.5 + 0.5 * thc(2., length(p.yz) * pi / 8.));
        col *= pal(0.1 * iTime - length(p) * 0.15, vec3(0,1,2)/3.);
        col = mix(bg(rd, p), col, 1. / cosh(0.5 * length(p)));

    } else {
        col = bg(rd, p);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 d) {
    return 0.5 + 0.5 * cos(2. * pi * (0.5 * t + d));
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b * cos(2. * pi * (c * t + d));
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float sfloor(float a, float b) {
    return floor(b) + 0.5 + 0.5 * tanh(a * (fract(b) - 0.5)) / tanh(0.5 * a);
}

// Stolen from iq, k = 0.12 is good
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) {
    float h = clamp(0.5 - 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) + k * h * (1. - h); 
}
