//SHOW

bool isKeyToggled(int KEY)
{
	return texelFetch( iChannel1, ivec2(KEY,0), 0 ).x > 0.5;
}

void mainImage( out vec4 c, in vec2 p )
{
    initialize(p, iFrame, iResolution);
    if(isKeyToggled(KEY_SPACE))
    {
        vec4 col = texelFetch(iChannel0, ivec2(p), 0);
        float k = float(pixelID(col.xy))/pixelnum;

        c = vec4(k);
    }
    else
    {
        if(all(greaterThan(vec2(50,50),p)))
        {
            vec4 col = texture(iChannel0, p/50.0);
            float k = float(pixelID(col.xy))/pixelnum; 
            c = vec4(k);
        }
        else
        {
            vec4 col = texelFetch(iChannel2, ivec2(p), 0);
            float exposure = 1. - (iMouse.x/R.x);
            c.xyz = tanh(2.0*exposure*pow(col.xyz/col.w,vec3(0.561,0.561,0.561)));
        }
    }

}

#define PI 3.14159265
#define TWO_PI 6.28318530718

vec2 R;
int sort_iters;
float pixelnum;

vec2 toScreen(vec2 p)
{
    return (0.5*(p - vec2(-0.5, 0.0)))*R.y + R*0.5;
}

bool onScreen(vec2 p)
{
    return all(greaterThan(p, vec2(0))) && all(greaterThan(R,p));
}


int pixelID(vec2 pix)
{
    return int(pix.x) + int(pix.y)*int(R.x);
}

ivec2 pixel(int pid)
{
    return ivec2(pid%int(R.x), pid/int(R.x));
}

//thanks rory618 (https://www.shadertoy.com/view/WssfDn) for this incredible magic function
int getPartner(int x, int s){
    float j = floor(sqrt(float(2*s)+1.25)-0.5);		//Major stage
    float n = floor(float(s) - 0.5*(j*j+j) + 0.5);	//Minor stage
    float b = floor(exp2(j-n+1.)+0.5);				//Block size
    float bot = floor(float(x)/b)*b;				//Bottom index in block 
    float top = bot + b - 1.;						//Top index in block
    if(n<0.5){										//Swap with opposite index in block
        return int(top - (float(x)-bot) + 0.5);
    }else{											//Swap with index a constant distance away in block
        if(float(x)-bot < b/2.-0.5){
            return int(float(x) + b/2. + 0.5);
        }else{
            return int(float(x) - b/2. + 0.5);
        }
    }
}


//internal RNG state 
uvec4 s0; 

void initialize(inout vec2 p, int frame, vec3 res)
{
    p = floor(p);

    R = res.xy;
    pixelnum = R.x*R.y;
    float lg2 = ceil(log2(pixelnum));
    sort_iters = 3*int(ceil(lg2*(lg2 + 1.0)/6.0) + 1.0);

    //white noise seed
    s0 = uvec4(p, uint(frame), uint(p.x) + uint(p.y));
}

// https://www.pcg-random.org/
uvec4 pcg4d(inout uvec4 v)
{
	v = v * 1664525u + 1013904223u;
    v.x += v.y*v.w; v.y += v.z*v.x; v.z += v.x*v.y; v.w += v.y*v.z;
    v = v ^ (v>>16u);
    v.x += v.y*v.w; v.y += v.z*v.x; v.z += v.x*v.y; v.w += v.y*v.z;
    return v;
}

float rand(){ return float(pcg4d(s0).x)/float(0xffffffffu); }
vec2 rand2(){ return vec2(pcg4d(s0).xy)/float(0xffffffffu); }
vec3 rand3(){ return vec3(pcg4d(s0).xyz)/float(0xffffffffu); }
vec4 rand4(){ return vec4(pcg4d(s0))/float(0xffffffffu); }

vec2 nrand2(float sigma, vec2 mean)
{
	vec2 Z = rand2();
    return mean + sigma * sqrt(-2.0 * log(Z.x)) * 
           vec2(cos(TWO_PI * Z.y),sin(TWO_PI * Z.y));
}

vec2 cmul(vec2 x, vec2 y)
{
    return vec2(x.x*y.x - x.y*y.y, x.x*y.y + x.y*y.x);
}

vec2 disk()
{
    vec2 r = rand2();
    return vec2(sin(TWO_PI*r.x), cos(TWO_PI*r.x))*sqrt(r.y);
}

float saturate (float x)
{
    return min(1.0, max(0.0,x));
}
vec3 saturate (vec3 x)
{
    return min(vec3(1.,1.,1.), max(vec3(0.,0.,0.),x));
}

vec3 bump3y (vec3 x, vec3 yoffset)
{
	vec3 y = vec3(1.,1.,1.) - x * x;
	y = saturate(y-yoffset);
	return y;
}
vec3 spectral_zucconi (float w)
{
    // w: [400, 700]
	// x: [0,   1]
	float x = saturate((w - 400.0)/ 300.0);

	const vec3 cs = vec3(3.54541723, 2.86670055, 2.29421995);
	const vec3 xs = vec3(0.69548916, 0.49416934, 0.28269708);
	const vec3 ys = vec3(0.02320775, 0.15936245, 0.53520021);

	return bump3y (	cs * (x - xs), ys);
}


const int max_iterations = 256;
const vec4 color_threshholds = vec4(255,130,80,255);

vec3 buddhabrot(int iters)
{
    vec2 z = vec2(0.0);
    vec2 c = disk()*1.5 + vec2(-0.5, 0.0);

    bool t = true;
    int i = 0;
    for(; i < iters; i++)
    {
        if(length(z) > 4.0) break;

        z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
    }

    if(i >= iters) return vec3(1e5);

    int maxj = int(rand()*1000.0)%i;
    z = vec2(0.0);
    for(int j = 0; j <= maxj; j++)
    {
         z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
    }
    return vec3(z, i);

}

