//BITONIC SORT

void mainImage( out vec4 c, in vec2 p )
{
    initialize(p, iFrame, iResolution);

    int sort_iter = 3*iFrame + 0;

    int id0 = pixelID(p);
    int id1 = getPartner(id0, sort_iter%sort_iters - 1);

    if(id1 < int(pixelnum) && id1 >= 0)
    {
        vec4 A = texelFetch(iChannel0, pixel(id0), 0);
        vec4 B = texelFetch(iChannel0, pixel(id1), 0);

        int zA = pixelID(A.xy);
        int zB = pixelID(B.xy);

        if(id0 > id1) c = (zA>zB)?A:B;
        else c = (zA<zB)?A:B;
    }
    else c = texelFetch(iChannel0, ivec2(p), 0);


    if(sort_iter%sort_iters == 0) //intialize with some random points, in this case buddhabrot samples
    {
        vec3 bp = vec3(-1);
        for(int i = 0; i < 64; i++)
        {
            bp = buddhabrot(256);
            bp.z = (bp.z > 20.)?(380.0 + 6.0*(bp.z - 20.0)):1000.0;
            bp.xy = toScreen(bp.xy);
            if(onScreen(bp.xy) && bp.z < 1000.) break;
        }
        bp.xy = clamp(bp.xy, vec2(-1), R);
        c = vec4(bp, 0.5); 
    }
}

