//BINARY SEARCH OF POINTS THAT FELL INTO THIS PIXEL

int search(int id)
{
    ivec2 range = ivec2(0, pixelnum);

    for(int i = 0; i < 64; i++)
    {
        //check the middle of the range
        int middle = (range.x + range.y)/2;
        vec4 data = texelFetch(iChannel1, pixel(middle), 0);
        //get point id
        int pid = pixelID(data.xy);

        //if found then stop
        if(pid == id) return middle;

        if(pid > id) //if our id of interest is below the found id
            range.y = middle; //then set the end of the range as middle
        else //if our id we search is above
            range.x = middle; //then set the beginning of the range as middle
    }

    return -1; //nothing found;
}

const vec3 C = vec3(0.133,0.141,0.184);

void mainImage( out vec4 c, in vec2 p )
{
    initialize(p, iFrame, iResolution);

    c = texelFetch(iChannel0, ivec2(p), 0);

    int sort_iter = 3*iFrame + 2;

    if((sort_iter+1)%sort_iters == 0) //if last sorting step
    {
        int pixid = pixelID(p);
        int id = search(pixid);

        vec3 col = vec3(0.0);
        if(id >= 0) //found at least 1 point in this pixel
        {
             for(int j = 0; j < 32; j++) //go left
            {
                int idx = id + j;
                vec4 data = texelFetch(iChannel1, pixel(idx), 0);
                int pid = pixelID(data.xy); //get point id
                if(pid!=pixid) break; //not in this pixel anymore
                c += vec4(spectral_zucconi(data.z), 0.0);

            }
            for(int j = 0; j < 32; j++) //go right
            {
                int idx = id - j - 1;
                vec4 data = texelFetch(iChannel1, pixel(idx), 0);
                int pid = pixelID(data.xy); //get point id
                if(pid!=pixid) break; //not in this pixel anymore
                c += vec4(spectral_zucconi(data.z), 0.0);
            }

        }
        c.w++;
    }

    if(iFrame == 0) c = vec4(0);
}
